    {
        // If Pressure is null/not set, we display unknown with no unit.
        string pressureMb = Pressure is null ? "unknown" : $"{Pressure} mb";
        return $"{{ Temperature = {Temperature}Â°C, Humidity = {Humidity}%, Pressure = {pressureMb} }}";
    }
}
